package com.practice;

public class Practice {
	


		
		//we are doing arthimetic operation
		
		
		
		
		public int multiplication(int a, int b) {
			return a*b;
			
		}
		
		public double division(double a, double b) {
			return a/b;
		}
		public int subtraction(int a, int b) {
			return a-b;
		}
		
		public double addition(double a, double b) {
			return a+b;
		}
		public static void main(String[] args) {
			// TODO Auto-generated method stub
			Practice c= new Practice();
			
			System.out.println(c.multiplication(12,13));
			System.out.println(c.addition(12, 10));
			System.out.println(c.subtraction(50, 26));
			System.out.println(c.division(15, 2));
		}

	}
	


